# -*- coding: utf-8 -*-
import base64, struct
bmp_data = base64.b64decode('')
def bmp_info(data):
    #bdata=base64.b64decode(data)
    #print(data)
    blist=struct.unpack('<ccIIIIIIHH', data[:30])
    if blist[0]==b'B' and (blist[1]==b'A' or blist[1]==b'M'):
        return {
            'width': blist[6],
            'height': blist[7],
            'color': blist[9]
        }
# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')